/**
 * This component shows a list of Tasks
 * @class TasksListComponent
 */
@Component({
    selector: 'app-tasks-list',
    templateUrl: './tasks-list.component.html',
    styleUrls: ['./tasks-list.component.css']
})
export class TasksListComponent implements OnInit {
    list: Tasks[];
    search: any = {};
    idSelected: string;
    constructor(
        private tasksService: TasksService,
    ) { }

    /**
     * Init
     */
    ngOnInit(): void {
        this.tasksService.list().subscribe(list => this.list = list);
    }

    /**
     * Select Tasks to remove
     *
     * @param {string} id Id of the Tasks to remove
     */
    selectId(id: string) {
        this.idSelected = id;
    }

    /**
     * Remove selected Tasks
     */
    deleteItem() {
        this.tasksService.remove(this.idSelected).subscribe(data => this.list = this.list.filter(el => el._id !== this.idSelected));
    }

}
