// Import Mongoose
import mongoose from "mongoose";
// Logging
import Logger from "./Logger";
// Properties
import properties from "../properties.js";

// Start Import Models

import CandidateModel from "../models/EasyRecruit_db/CandidateModel";
import DocumentsModel from "../models/EasyRecruit_db/DocumentsModel";
import TaskListModel from "../models/EasyRecruit_db/TaskListModel";
import TasksModel from "../models/EasyRecruit_db/TasksModel";
import UserModel from "../models/EasyRecruit_db/UserModel";

// End Import Models

// Import Models Factom Blockchain
import IdentityModel from "../models/EasyRecruit_db/IdentityModel";
import ChainModel from "../models/EasyRecruit_db/ChainModel";
import EntryModel from "../models/EasyRecruit_db/EntryModel";

class Database {
  constructor() {}

  /**
   * Init database
   */
  async init() {
    await this.authenticate();
    Logger.info("MongoDB connected at: " + properties.easyRecruit_db_dbUrl);

    // Start Init Models

		CandidateModel.init();
		DocumentsModel.init();
		TaskListModel.init();
		TasksModel.init();
		UserModel.init();
 // End Init Models
    
    // Init Models Factom Blockchain
    IdentityModel.init();
    ChainModel.init();
    EntryModel.init();
  }

  /**
   * Start database connection
   */
  async authenticate() {
    Logger.info("Authenticating to the databases...");
    try {
      this.dbConnection_easyRecruit_db = await mongoose.connect(
        "mongodb://" + properties.easyRecruit_db_dbUrl,
        { useNewUrlParser: true }
      );
    } catch (err) {
      Logger.error(`Failed connection to the DB: ${err.message}`);
      Logger.error(err);
      await new Promise(resolve => setTimeout(resolve, 5000));
      await this.authenticate();
    }
  }

  /**
   * Get connection db
   */
  getConnection() {
    return this.dbConnection_easyRecruit_db;
  }
}

export default new Database();
